import {TouchPopupLayout} from '../index';

export default class DatePickerTouchPopupLayout extends TouchPopupLayout {

  constructor(popup) {
    super(popup);
  }

  /**
   * @override
   */
  layout($container) {
    super.layout($container);
    this.popup.getDatePicker()._layoutWeekendSeparators();
  }
}
